casper.test.begin('Widget - Avocet Footer', function(test) {

    var verifyFooter = function() {
        // Verify that the footer exists on the page
        test.assertExists('#oa-avocetfooter', 'Verify the footer exists on the page');
        // Verify that the footer has a link to the feedback form
        test.assertExists('#oa-avocetfooter .oa-avocetfooter-links button.oa-btn-feedback', 'Verify the footer has a link to the feedback form');
        // Verify that the footer has a contact link
        test.assertExists('#oa-avocetfooter .oa-avocetfooter-links a[href="mailto:info@openaccess.cam.ac.uk"]', 'Verify the footer has a contact link');
    };

    casper.start(configUtil().tenantUI, function() {
        // Verify that the footer is showing
        casper.then(function() {
            casper.echo('# Verify that the footer shows on the page', 'INFO');
            casper.waitForSelector('#oa-avocetfooter', verifyFooter);
        });
    });

    casper.run(function() {
        test.done();
    });
});
